import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import StepContent from '@material-ui/core/StepContent';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    marginLeft:"35%"
  },
  button: {
    marginTop: theme.spacing(1),
    marginRight: theme.spacing(1),
  },
  actionsContainer: {
    marginBottom: theme.spacing(2),
  },
  resetContainer: {
    padding: theme.spacing(3),
  },
}));

function getSteps() {
  return ['Jul 2020 - Dec 2020', 'Dec 2020 - Oct 2021','Oct 2021 -' ];
}

function getStepContent(step) {
  switch (step) {
    case 0:
      return (
          <p>
              Intern - Kunshan Kersen Science &amp; Technology Co., Ltd. <br />
              <h4> Major work &amp; Achivements:</h4>
              <p style={{width:'30%'}}>
                  Had exposure to the algorithm behind digital image processing.
                  Experienced image manipulation with OpenCV. 
              </p>
              
          </p>
      );
    case 1:
      return (
          <p>
              Full-time - Kunshan Kersen Science &amp; Technology Co., Ltd. <br />
              <h4> Major work:</h4>
              <p style={{width:'30%'}}>Developed finance report generation application with Qt and c++. Assisted the development of 
                digital image processing application. Implemented modules such as Rectangle/Cicle Calliper. 
                Communicated with clients about business requirements and participated in the updates of the finance report generation software.
              </p>
          </p>
      );
    case 2:
      return (
          <p>
              Fulle-time - QAD <br />
              <h4> Major work:</h4>
              Perform CRUD actions on data entities and fix existing bugs on applications.<br />
              Transform legacy desktop application code into web application<br />
              Manage ERP system implementation modules.<br />
              Apply Agile Software development in real-world project.<br />
              Experience with modern CI/CD development tools such as Bamboo.<br />
              Cooperate with developers to improve QAD’s current features.<br />
              Learn how to manage task and project via Jira.<br />
          </p>
      ) 
    default:
      return 'Unknown step';
  }
}

export default function ProjectInforItem() {
  const classes = useStyles();
  const [activeStep, setActiveStep] = React.useState(0);
  const steps = getSteps();

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleReset = () => {
    setActiveStep(0);
  };

  return (
    <div className={classes.root}>
      <Stepper activeStep={activeStep} orientation="horizontal">
        {steps.map((label, index) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
            <StepContent>
              <Typography>{getStepContent(index)}</Typography>
              <div className={classes.actionsContainer}>
                <div>
                  <Button
                    disabled={activeStep === 0}
                    onClick={handleBack}
                    className={classes.button}
                  >
                    Back
                  </Button>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={handleNext}
                    className={classes.button}
                  >
                    {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                  </Button>
                </div>
              </div>
            </StepContent>
          </Step>
        ))}
      </Stepper>
      {activeStep === steps.length && (
        <Paper square elevation={0} className={classes.resetContainer}>
          <Typography>All steps completed - you&apos;re finished</Typography>
          <Button onClick={handleReset} className={classes.button}>
            Reset
          </Button>
        </Paper>
      )}
    </div>
  );
}
